package javacore;

public class OddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Odd numbers from 1 to 100");
		
		for (int i = 1; i <= 100; i++) {
			if (i%2 != 0) {
				System.out.print(i + " ");
			}
		}

	}

}
